import React, { Component } from 'react'
import { SafeAreaView, Text, TouchableOpacity, View, Image, StyleSheet, Dimensions } from 'react-native'
var ms = Dimensions.get('window');
import Profile_Image from './../assets/profile_pic.png'
import Settings from './../assets/settings.png'


export default class App extends Component {
  constructor() {
    super()
    this.state = {
      profileMenu:'none',
      shadowValue:0,
      backColor:'',
      // backColor:'#d3d3d3',
    }
  }
  remo = () => {
    if (this.state.profileMenu=='none') {
      this.setState({
        'profileMenu':'flex',
        'shadowValue': 1,
        'backColor':'lightblue'

      })
    } else {
      this.setState({
        'profileMenu':'none',
        'shadowValue':0,
        'backColor':''
      })        
    }
    
  }

  render() {
    var shadowed = StyleSheet.flatten([
      styles.header,{
        shadowOpacity:this.state.shadowValue,
        backgroundColor:this.state.backColor
      }
    ])
    var boxContainer = StyleSheet.flatten([
      styles.picContainer,{
        shadowOpacity:this.state.shadowValue,        
      }
    ])
    var menuContainter = StyleSheet.flatten([
        styles.secContainer,{
            display:this.state.profileMenu
        }
    ])
    return (
      <SafeAreaView style={styles.mainContainer}>
        <View style={shadowed}>
          <TouchableOpacity onPress={() => this.remo()} style={boxContainer}>
            <Image source={Profile_Image} style={styles.logo} />
          </TouchableOpacity>
          <View style={menuContainter}>
            <Text style={styles.welcometext}>Hello {this.props.name}!</Text>
            <TouchableOpacity style={styles.profileButton}>
                <Text style={{paddingHorizontal:10}}>View Profile</Text>
            </TouchableOpacity>
            <TouchableOpacity style={styles.settings}>
                <Image source={Settings} style={styles.logo} />
            </TouchableOpacity>
          </View>
        </View>
      </SafeAreaView>
    )
  }
} 

const styles = StyleSheet.create({
  mainContainer:{
    marginVertical:5,
    flex:1,
    alignItems:'center',
    // backgroundColor:'white',
  },
  header: {
    width:ms.width*8/9,
    shadowRadius:1,
    shadowOffset:{height:0.1},
    height:50,
    borderRadius:25,
    flexDirection:'row',
    alignItems:'center'
  },
  picContainer:{
    width:40,
    height:40,
    marginLeft:5,
    borderRadius:20,
    backgroundColor:'white',
    shadowRadius:1,
    shadowOffset:{height:0.1},
    justifyContent:'center',
    alignItems:'center',
  },
  logo:{
    width:40,
    height:40,
  },
  secContainer: {
      width:ms.width*8/9 - 50,
      flexDirection:'row',
      alignItems:'center',
      justifyContent:"space-between",
  },
  welcometext:{
    marginHorizontal:10,
  },
  profileButton:{
      justifyContent:'center',
      alignItems:'center',
      height:35,
      shadowRadius:1,
      shadowOpacity:0.5,
      borderRadius:2,
      backgroundColor:'lightblue',
      shadowOffset:{height:0.1}, 
  },
  settings:{
    shadowOpacity:1,
    shadowRadius:1,
    shadowOffset:{height:0.1},
  }
})
